import React from "react";
import axios from "axios";
class User extends React.Component {
  state = {
    users: [],
    error: null,
  };
  componentDidMount() {
    const users = axios
      .get("https://reqres.in/api/users?page=1")
      .then((res) => {
        console.log(res);
      });
  }
  render() {
    const { users, error } = this.state;
    if (error) {
      return <>{error}</>;
    } else {
      return (
        <div className="container">
          {users && users ? (
            users.map((user, i) => {
              <div key={user.id}>
                <span>user.id</span>
              </div>;
            })
          ) : (
            <>
              <p>Không tìm thấy user nào</p>
            </>
          )}
        </div>
      );
    }
  }
}
export default User;
