import React from "react";

class Todo extends React.Component {
  state = {
    task: "",
    arrTasks: [
      {
        id: 12121212,
        title: "Task 1",
      },
      {
        id: 12123131,
        title: "Task 2",
      },
      {
        id: 121144421212,
        title: "Task 3",
      },
    ],
  };
  handleOnchangeTask = (event) => {
    this.setState({
      task: event.target.value,
    });
  };
  render() {
    let { menuObj } = this.props;
    let { arrTasks } = this.state;
    console.log(arrTasks);
    if (menuObj.menu === "home") {
      return (
        <>
          <p>Homeeeeeeeeeeeeeeeee</p>
        </>
      );
    }
    if (menuObj.menu === "todo") {
      return (
        <>
          <div>
            <p>There are my tasks</p>
            <div className="input-div">
              <input
                type="text"
                className="form-control"
                placeholder="Input task here"
                value={this.state.task}
                onChange={(event) => {
                  this.handleOnchangeTask(event);
                }}
              />
              <button className="btn btn-primary" disabled={!this.state.task}>
                ADD
              </button>
            </div>

            <div className="list-group">
              {arrTasks.map((item, index) => {
                return (
                  <div key={item.id}>
                    <a className="list-group-item">{item.title}</a>
                  </div>
                );
              })}
            </div>
          </div>
        </>
      );
    }
  }
}
export default Todo;
