import React from "react";
import MenuDetails from "./MenuDetails";
class MenuList extends React.Component {
  state = {
    menu: "",
    clickedMenu: false,
  };
  handleOnClickHome = () => {
    this.setState({
      menu: "home",
    });
  };
  handleOnClickTodo = () => {
    this.setState({
      clickedMenu: true,
    });
    this.setState({
      menu: "todo",
    });
  };
  render() {
    const { clickedMenu } = this.state;
    const textStyle = clickedMenu ? { color: "red" } : {};
    return (
      <>
        <div>
          <ul className="breadcrumb">
            <li>
              <a
                href="#a"
                onClick={() => {
                  this.handleOnClickHome();
                }}
                style={textStyle}
              >
                Home
              </a>
            </li>
            <li>
              <a
                href="#a"
                onClick={() => {
                  this.handleOnClickTodo();
                }}
                style={textStyle}
              >
                Todo
              </a>
            </li>
          </ul>
        </div>
        <div>
          <MenuDetails menuObj={this.state} />
        </div>
      </>
    );
  }
}
export default MenuList;
