import React from "react";
function writeLog(content) {
  // Đường dẫn đến tệp cần ghi
  const filePath = "ten-tap-tin.txt";

  // Kiểm tra xem tệp đã tồn tại chưa
  fetch(filePath)
    .then((response) => {
      // Nếu tệp tồn tại, thêm tùy chọn để ghi tiếp vào cuối nó
      const options = response.ok ? { mode: "append" } : {};

      return fetch(filePath, {
        ...options,
        method: "PUT",
        body: content,
      });
    })
    .then(() => {
      console.log("Đã ghi dữ liệu vào tệp thành công.");
    })
    .catch((error) => {
      console.error("Lỗi khi ghi tệp:", error);
    });
}
const Logger = (Component) => {
  return (props) => {
    <>
      <Component {...this.props} onClick={writeLog("Da ghi")} />
    </>;
  };
};

export default Logger;
