  //           const users2 = res2.data.data;
  //           //Kết hợp 2 mảng thành 1
  //           const combinedUsers = [...users1, ...users2];
  //           this.setState({
  //             users: combinedUsers,
  //           });
  //         })
  //         .catch((error2) => {
  //           this.setState({
  //             error: error2.message + " : page2",
  //           });
  //         });
  //       this.setState({
  //         users: users1,
  //       });
  //     })
  //     .catch((error1) => {
  //       this.setState({
  //         error: error1.message + " : page1",
  //       });
  //     });
  // }
  render() {
    const { users, error } = this.state;
    if (error) {
      return <>{error}</>;
    } else {
      return (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Avatar</th>
              <th>Email</th>
              <th>Full Name</th>
            </tr>
          </thead>
          <tbody>
            {users && users.length > 0 ? (
              users.map((user, i) => {
                return (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>
                      {" "}
                      <img
                        src={user.avatar}
                        alt={user.avatar}
                        onClick={() => this.handleOnclickImg(user.id)}
                      />
                    </td>
                    <td>{user.email}</td>
                    <td>{`${user.first_name} ${user.last_name}`}</td>
                  </tr>
                );
              })
            ) : (
              <>
                <p>Không tìm thấy user nào</p>
              </>
            )}
          </tbody>
        </table>
      );
    }
  }
}
export default User;
