import React from "react";
import axios from "axios";
import "./user.scss";
class User extends React.Component {
  state = {
    users: [],
    error1: null,
    error2: null,
  };
  componentDidMount() {
    axios
      .get("https://reqres.in/api/users?page=1")
      .then((res1) => {
        const users1 = res1.data.data;

        axios
          .get("https://reqres.in/api1/users?page=2")
          .then((res2) => {
            const users2 = res2.data.data;
            //Kết hợp 2 mảng thành 1
            const combinedUsers = [...users1, ...users2];
            this.setState({
              users: combinedUsers,
            });
          })
          .catch((error2) => {
            this.setState({
              error2: error2.message,
            });
          });
        this.setState({
          users: users1,
        });
      })
      .catch((error1) => {
        this.setState({
          error1: error1.message,
        });
      });
  }
  render() {
    const { users, error1, error2 } = this.state;
    if (error1 || error2) {
      return <>{error1}</>;
      return <>{error2}</>;
    } else {
      console.log(">>>Check users", users);
      return (
        <div className="container list-users">
          {users && users.length > 0 ? (
            users.map((user, i) => {
              return (
                <div className="row sub-users" key={user.id}>
                  <span>{user.id}</span>
                  <img src={user.avatar} alt={user.avatar} />
                  <span>{user.email}</span>
                  <span>{`${user.first_name} ${user.last_name}`}</span>
                </div>
              );
            })
          ) : (
            <>
              <p>Không tìm thấy user nào</p>
            </>
          )}
        </div>
      );
    }
  }
}
export default User;
