import Nav from "./nav/Nav";
import Home from "./home/Home";
import About from "./about/About";
import TodoList from "./todo/TodoList";
import Title from "./title/Title";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
function countDown() {
  let { s } = this.state;
  // Sử dụng setInterval để lặp lại đoạn mã, hàm
  // {
  //   if (s > 0) {
  //     this.setState({
  //       s: s,
  //     });
  //     s = s - 1;
  //   } else {
  //     clearInterval(intervalId);
  //   }
  // }
  // sau mỗi 1000 milliseconds (1 giây) được truyền vào
  const intervalId = setInterval(() => {
    if (s >= 0) {
      this.setState({
        s: s,
      });
      s = s - 10;
      return s;
    } else {
      // Để dừng lặp lại, bạn có thể sử dụng clearInterval với intervalId
      clearInterval(intervalId);
      console.log(">>>Check s", s);
      console.log(">>>Check props", this.props);
      return s;
    }
  }, 10);
}
function App() {
  return (
    <Router>
      <Nav />
      <header className=" container jumbotron jumbotron-fluid">
        <div className="container text-center">
          <Title />
        </div>
      </header>
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <Switch>
              <Route path="/" exact>
                <Home />
              </Route>
              <Route path="/todo" component={TodoList} />
              <Route path="/about" component={About} />
            </Switch>
          </div>
        </div>
      </div>
      {/* <!-- Phần chân trang --> */}
      <footer className="footer">
        <div className="container">
          <p>&copy; 2023 Phạm Thanh Tùng</p>
        </div>
      </footer>
    </Router>
  );
}

export default App;
