import React from "react";

class Todo extends React.Component {
  state = {
    task: "",
    arrTasks: [
      {
        id: 12121212,
        title: "Task 1",
      },
      {
        id: 12123131,
        title: "Task 2",
      },
      {
        id: 121144421212,
        title: "Task 3",
      },
    ],
  };
  handleOnchangeTask = (event) => {
    this.setState({
      task: event.target.value,
    });
  };
  handleOnclickAdd = (task) => {
    this.setState({
      task: {
        id: 2,
        title: this.state.task,
      },
      arrTasks: [...this.state.arrTasks, task],
    });
  };
  render() {
    let { menuObj } = this.props;
    let { arrTasks } = this.state;
    console.log(arrTasks);
    switch (menuObj.menu) {
      case "home":
        return (
          <>
            <p>Homeeeeeeeeeeeeeeeee</p>
          </>
        );
      case "todo":
        return (
          <>
            <div>
              <p>There are my tasks</p>
              <div className="input-div">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Input task here"
                  value={this.state.task}
                  onChange={(event) => {
                    this.handleOnchangeTask(event);
                  }}
                />
                <button
                  className="btn btn-primary"
                  disabled={!this.state.task}
                  onClick={(a) => this.handleOnclickAdd(a)}
                >
                  ADD
                </button>
              </div>

              {arrTasks.map((item, index) => {
                return (
                  <div className="item-div list-group" key={item.id}>
                    <a className="list-group-item">{item.title}</a>
                    <button type="button" class="btn btn-danger">
                      Delete
                    </button>
                  </div>
                );
              })}
            </div>
          </>
        );
      default:
        break;
    }
  }
}
export default Todo;
