import React from "react";

class Todo extends React.Component {
  state = {
    task: "",
    arrTasks: [],
  };
  handleOnchangeTask = (event) => {
    this.setState({
      task: event.target.value,
    });
  };
  handleOnclickAdd = () => {
    let taskObj = {
      id: Math.floor(Math.random() * 1001),
      title: this.state.task,
    };
    this.setState({
      task: "",
      arrTasks: [...this.state.arrTasks, taskObj],
    });
  };
  handleOnClickDel = (id) => {
    this.setState({
      arrTasks: this.state.arrTasks.filter((item) => item.id !== id),
    });
  };
  render() {
    return (
      <>
        <div>
          <p>There are my tasks</p>
          <div className="input-div">
            <input
              type="text"
              className="form-control"
              placeholder="Input task here"
              value={this.state.task}
              onChange={(event) => {
                this.handleOnchangeTask(event);
              }}
            />
            <button
              className="btn btn-primary"
              disabled={!this.state.task}
              onClick={() => this.handleOnclickAdd()}
            >
              ADD
            </button>
          </div>

          {arrTasks.map((item, index) => {
            return (
              <div className="item-div list-group" key={item.id}>
                <a className="list-group-item">{item.title}</a>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => this.handleOnClickDel(item.id)}
                >
                  Delete
                </button>
              </div>
            );
          })}
        </div>
      </>
    );
  }
}
export default Todo;
